    let zipped = props.game1LeaderBoardIndex.map((x, i) => [x, props.game1LeaderBoardUsername[i]]);
    let zipped2 = zipped.map((x, i) => [x, props.game1LeaderBoardStage[i]]);
    let zipped3 = zipped2.map((x, i) => [x, props.game1LeaderBoardTries[i]]);










  const userList = zipped3.map((users) => {

