      </Helmet>




      <div className="siteTitle">Hmmmm invisible ink!
      </div>



      <Container className='mt-5' fluid="md">
        <Card className='cardRounded'>



          <Card.Header>Congratulation Explorer</Card.Header>
          <Card.Body>
            <blockquote className="blockquote mb-0">
              <p>
                {' '}
                <br/><br/>THIS PAGE WILL SELF DISTRUCT ONCE YOU CLOSE IT...
                <br/><br/>If you combine the answer to 1 2 3 + Something old you will get me. Two Words joined! +/- a Month? The final word is 200 million years old? It's out of this world!
<br/><br/>

                {' '}
              </p>
              <footer className="blockquote-footer">
                Captain Troy 'Mighty' Armstong
              </footer>
            </blockquote>
          </Card.Body>
        </Card>

      </Container>





      </div>

      </div>




    );

}

export default Level2Game1;
