// App.js

import React, {useState} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";


const Connection = (props) =>  {

  const wallet = useWallet()

  const [account,setAccountName] = useState(false)

  // if (props.accounts){
  //   setAccountName(true)
  // }


  console.log("this here",props.accounts)

  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()


    // setAccountName(true)

  }

  return (

    <>
      {wallet.status === 'connected' ? (
        <div>
          <div>Account: {wallet.account}</div>
          <div>Balance: {wallet.balance}</div>
          <button onClick={() => wallet.reset()}>disconnect</button>
        </div>
      ) : (
        <div>
          Connect:
          <button onClick={connectWallet}>MetaMask</button>
          <button onClick={() => wallet.connect('frame')}>Frame</button>
          <button onClick={() => wallet.connect('portis')}>Portis</button>
        </div>
      )}
    </>
  )

}


// Wrap everything in <UseWalletProvider />
export default () => (
  <UseWalletProvider
    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}>



    <Connection />
  </UseWalletProvider>
)
