import React from 'react';
import Timer from './Timer';

import img1 from './images/elons-rocket.jpg';
import img2 from './images/fortblox.png';
import img3 from './images/elons_lost_his_rocket.png';
import img4 from './images/elons_lost_his_rocket.png';



import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './HomeCard.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


const HuntWowCards = (props) => {

  const commaNumber = require('comma-number')


  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_time_lock_cost = props.game1_time_lock_cost;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;


  const allGame1_deadline_time = props.allGame1_deadline_time;

  // console.log(game1_id)s

  const renderTooltip = (props) => (


  <Tooltip id="button-tooltip" {...props}>
    Once you have purchased your Blox they buy you 30 seconds to enter your answer, no one else can answer in this time and this stops bots from beating you to it!
  </Tooltip>
);




  return(



    <div>


          <Card className='cardRounded'>
            <Card.Body className="customBodyTwo">
              <Card.Title className="gameTitle">Costs</Card.Title>
              <div >Simply purchase Blox to enter this game. The entry fee is a one off purchase. Time Lock and Secret submission cost apply each time you use them.</div>
              <br/>
              <div className="descriptionTitle">Cost to Enter NEEDS CHANGING {game1_time_lock_cost} BLOX</div>
              <div className="descriptionTitle">Cost to Lock Time {game1_time_lock_cost} BLOX</div>
              <div className="descriptionTitle">Cost to submit Secret {props.game1_submit_secret_cost} BLOX</div>
<br/>

            </Card.Body>
          </Card>

    </div>



  );


};

export default HuntWowCards;
