import React, {useState} from 'react';
import Timer from './Timer';

import img1 from './images/elons-rocket.jpg';
import img2 from './images/fortblox.png';
import img3 from './images/elons_lost_his_rocket.png';
import img4 from './images/elons_lost_his_rocket.png';



import {Spinner,Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './Step2Card.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


const Step2Card = (props) => {

  const commaNumber = require('comma-number')
  const keccak256 = require('keccak256')


  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_time_lock_cost = props.game1_time_lock_cost;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;


  const [secret,setSecret] = useState(undefined);
  const [message,setMessage] = useState(undefined);
  const [hashedSecret,setHashedSecret] = useState(undefined);



  const [loading,setloading] = useState(false);


  const allGame1_deadline_time = props.allGame1_deadline_time;
  const contract = props.contract
  const accounts = props.accounts

  console.log("Contract",contract)
  console.log("Accounts",accounts)
  console.log("secret updated: ",secret)
  console.log("message updated: ",message)

  console.log("allGame1_id updated: ",props.allGame1_id)

  console.log("hashed secret: ",hashedSecret);
  console.log("Message: ",message);


    const handleInputChangeSecret = (event) => {
      const target = event.target;
      const value = target.type === "checkbox" ? target.checked : target.value;
      const secret = target.secret;
      console.log("hashed secret: ","0x"+keccak256(value).toString('hex'));
      const userSecret = "0x"+keccak256(value).toString('hex');
      setSecret(value);
      setHashedSecret(userSecret)

    }

    const handleInputChangeMessage = (event) => {
      const target = event.target;
      const value = target.type === "checkbox" ? target.checked : target.value;
      const message = target.message;
      setMessage(value);
      console.log("Message: ",message);
    }

    const handleSubmit = async() => {
      setloading(true);
      console.log("START");
      console.log(hashedSecret);
      console.log(secret);
      console.log(props.allGame1_id);

      console.log(message);
      console.log("ENDS");

      // let hased_secret = "0x"+keccak256(secret).toString('hex');
      // console.log(keccak256(secret).toString('hex'));
      // hasedConfirmed
      // _answer
      // _treasureGameid
      // _winningMessage
      // await contract.methods.SubmitSecret(hashedSecret,secret,props.allGame1_id,message).send({from: accounts});
      setSecret(undefined);
      setMessage(undefined);

      console.log("I am the first log");

      setTimeout(function(){
          setloading(false);
      },3000);


    }


  return(
        <div>
            { loading ? (
              <div>
              <br/>
                <br/>
                  Verifying Time Lock Please Wait!
                  <br/>
                  <br/>
                  <Spinner animation="grow" variant="light" />
                <br/>
              <br/>
              </div>
            ) : (
              <div>
                <div className="descriptionTitle">YOU HAVE ENTERED!</div>
                <div className="gameTitleEnter">Use Time Lock Now!</div>
                <div>Know the answer? Get a 90 second time lock where only you can answer the secret!
                </div>
                <br/>
                <div className="descriptionTitle">90 second Time Lock Cost {game1_time_lock_cost} Blox</div>

                <br />
                <div>Enter a username</div>
                <Form.Control type="text" placeholder="Enter The Secret" name="secret" value={secret} onChange={handleInputChangeSecret}/>
                <Form.Control type="text" placeholder="Enter A Winning Message" name="message" value={message} onChange={handleInputChangeMessage}/>
                <br />
                <Button className="customButton" onClick={handleSubmit}>Lock Time Now</Button>
                <br />
                <br />
                <div>Game Attempts {props.allGame1_total_game_tries}</div>
              </div>



            )}
        </div>



  );


};

export default Step2Card;
