import React from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'

function Connection() {
  const wallet = useWallet()

  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()
  }

  return (

    <div><button onClick={connectWallet}>Connect Wallet</button>
    </div>

