// App.js

import React, {useState, useEffect} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";


const Connection = (props) =>  {

  const wallet = useWallet()
  const [address, setAddress] = useState('Connect Wallet');


  useEffect(() => {
     // Update the document title using the browser API
     const buttonText = `${address}`;


   });




  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()
    const usersAccount = props.accounts;
    const stringOfUsersAccount = new String(usersAccount)
    console.log(stringOfUsersAccount.slice(0, 5)); // "01"
    setAddress(stringOfUsersAccount.slice(0, 5));
  }








  return usersAccount?(
    <div>
      <Button onClick={connectWallet} className="customButton enableEthereumButton">Metamask{address}</Button>
    </div>

  ):
  (
    <div>
    address
      <Button onClick={connectWallet} className="customButton enableEthereumButton">{address}</Button>
    </div>
  )
}

// Wrap everything in <UseWalletProvider />
export default (props) => (
  <UseWalletProvider
    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}>

    <Connection accounts={props.accounts}/>



  </UseWalletProvider>
)
