      setIsGameOver(true)
    }

    const more = () => {
      clearInterval(gameTimerId)
      clearInterval(obstaclesTimerId)
      clearInterval(obstaclesTimerIdTwo)
      setIsGameOver(true)

    }

    setTimeout(function(){
    },1);
  }

  return (

    <div >
    {play?(
      <div>
      <Container className='mt-5' fluid="md">

      <div>
        <div className="siteTitle" style={{fontSize: '30px'}}>Your Score {score}</div>

        {isGameOver && <div className="siteTitle" style={{fontSize: '30px'}}>You Scored Bad Luck</div>}



        <Bird
          birdBottom = {birdBottom}
          birdLeft = {birdLeft}
        />

        <Obstacles
          color={'yellow'}
          obstacleWidth = {obstacleWidth}
          obstacleHeight = {obstacleHeight}
          randomBottom = {obstaclesNegHeight}
          gap = {gap}
          obstaclesLeft = {obstaclesLeft}
        />

        <Obstacles
          color={'green'}
          obstacleWidth = {obstacleWidth}
          obstacleHeight = {obstacleHeight}
          randomBottom = {obstaclesNegHeightTwo}
          gap = {gap}
          obstaclesLeft = {obstaclesLeftTwo}
        />


      </div>
      <Button className="customButton" onClick={jump}>Click To Jump</Button>
      <br/>
      </Container>
      </div>
    ):(

      <div>
      <Button className="customButton" onClick={handlePlay}>Start</Button>
      </div>

    )}


    </div>
  );
}

export default Screen;
