import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import MyNav from './components/Structure/Nav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component } from 'react'


// first we'll require web3
import Web3 from 'web3'

// next we'll create a new instance of web3.
var web3 = new Web3();

// Set the provider
web3.setProvider(new Web3.providers.WebsocketProvider('ws://localhost:8545')
);

const abi = require('./TreasureBlox.abi.json');
const mepAddress = '0xd9145CCE52D386f254917e481eB44e9943F39138'

let contract = new web3.eth.Contract(abi,mepAddress);


class App extends Component {

  state = { storageValue: 0,
     web3: null,
     accounts: null,
     contract: null,
     decimals:null,
     bal:null,
     owner:null,
     marketingWallet:null,
     games:[]};





  render() {

    return (
        <div className="background customFont">

          <MyNav />
          <SingleCard/>





          <MyCards/>
          <MyCards/>
      </div>
    );
  }
}

export default App;
