import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import MyNav from './components/Structure/Nav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component } from 'react'
import Web3 from 'web3'

class App extends Component {
  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    console.log("Accounts: ",accounts)
    buttonText = account;
  }



  constructor(props) {
    super(props)
    this.state = { account: '' }
  }

  componentWillMount() {
    this.loadBlockchainData()
  }








  render() {

    return (
        <div className="background customFont">

          <MyNav id="enableEthereumButton" account={buttonText}/>
          <SingleCard/>

          <MyCards/>
          <MyCards/>
      </div>
    );
  }
}

export default App;
