import React from 'react';
import Timer from './Timer';

import img1 from './images/img1.jpeg';
import img2 from './images/img2.jpg';
import img3 from './images/img3.jpeg';
import img4 from './images/img4.jpeg';



import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './HomeCard.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


const HomeCards = (props) => {

  const commaNumber = require('comma-number')


  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_cost_to_enter = props.game1_cost_to_enter;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;


  const allGame1_deadline_time = props.allGame1_deadline_time;



  // console.log(game1_id)s

  const renderTooltip = (props) => (


  <Tooltip id="button-tooltip" {...props}>
    Once you have purchased your Blox they buy you 30 seconds to enter your answer, no one else can answer in this time and this stops bots from beating you to it!
  </Tooltip>
);




  return(



    <Container className='mt-5' fluid="md">

<div className="spaceTop">
      <CardGroup className='mt-5'>
        <CardColumns >


          <Card className='cardRounded'>
            <Card.Body className="customBodyThree">
              <Card.Title className="gameTitle">How it works</Card.Title>

              <div className="descriptionTitle">Contract</div>
              <div className="descriptionBody">Number of people entered Number of people entered Number of people entered Number of people entered</div>
              <div className="descriptionTitle">White Paper</div>
              <div className="descriptionBody">Number of people entered Number of people entered Number of people entered Number of people entered</div>
              <div className="descriptionTitle">Telegram</div>
              <div className="descriptionBody">Number of people entered Number of people entered Number of people entered Number of people entered</div>
              <br/>
              <Link to="/hunt1">
                <Button className="tinyCustomButtonDescription" to="/hunt1">Buy Blox</Button>
              </Link>

              <Link to="/hunt1">
                <Button className="tinyCustomButtonDescription" to="/hunt1">View Quest</Button>
              </Link>

              <Link to="/hunt1">
                <Button className="tinyCustomButtonDescription" to="/hunt1">View Quest</Button>
              </Link>

            </Card.Body>
          </Card>

          <Card className='cardRounded'>
            <Card.Body className="customBodyTwo">
              <Card.Title className="gameTitle">Purchase Blox</Card.Title>
              <div>Riddle {props.game1_riddle}</div>
              <div>Prize {props.game1_prize} Blox</div>
              <div>Number of people entered</div>
              <br/>

              <Button className="customButton" >Buy Blox</Button>

              <br/><br/>
              <div className="postPrize">{props.allGame1_total_game_tries} Attempts <div className="prePrize">Hunt Launched 1 day ago</div></div>
            </Card.Body>
          </Card>


          <Card className='cardRounded'>
            <Card.Img variant="top" src={img2} alt="Logo" className='customRounded'/>
            <Card.Body >
              <Card.Title className="gameTitle">Hunt For Elon's Rocket</Card.Title>
              <div>Riddle {props.game1_riddle}</div>
              <div>Prize {props.game1_prize} Blox</div>
              <div>Number of people entered</div>
              <br/>
              <Link to="/hunt2">
                <Button className="customButton" to="/hunt1">View</Button>
              </Link>
              <br/><br/>
              <div className="postPrize">{props.allGame1_total_game_tries} Attempts <div className="prePrize">Hunt Launched 1 day ago</div></div>
            </Card.Body>
          </Card>

          <Card className='cardRounded'>
            <Card.Img variant="top" src={img3} alt="Logo" className='customRounded'/>
            <Card.Body >
              <Card.Title className="gameTitle">Hunt For Elon's Rocket</Card.Title>
              <div>Riddle {props.game1_riddle}</div>
              <div>Prize {props.game1_prize} Blox</div>
              <div>Number of people entered</div>
              <br/>
              <Link to="/hunt3">
                <Button className="customButton" to="/hunt1">Learn More</Button>
              </Link>
              <br/><br/>
              <div className="postPrize">{props.allGame1_total_game_tries} Attempts <div className="prePrize">Hunt Launched 1 day ago</div></div>
            </Card.Body>
          </Card>


          </CardColumns>
      </CardGroup>

    </div>
    </Container>


  );


};

export default HomeCards;
