// App.js

import React, {useState, useEffect} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button,ButtonGroup,Col,Toast,Modal } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";
import "./App.css";

import getWeb3 from "./getWeb3"


const Connection = (props) =>  {

  /* // When the modal is hidden... */
  const scrollY = document.body.style.top;
  document.body.style.position = '';
  document.body.style.top = '';
  window.scrollTo(0, parseInt(scrollY || '0') * -1);
  /* // When the modal is shown, we want a fixed body */
  document.body.style.position = 'fixed';
  document.body.style.top = `-${window.scrollY}px`;

  /* // When the modal is hidden, we want to remain at the top of the scroll position */
  document.body.style.position = '';
  document.body.style.top = '';


  const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);



  const wallet = useWallet()
  const [firstPartAddress, setFirstPartAddress] = useState('Connect Wallet');
  const [secondPartAddress, setSecondPartAddress] = useState('');

  useEffect(() => {

     const init = async() => {
       const web3 = await getWeb3();
       const accounts = await web3.eth.getAccounts();
       const usersAccount = accounts[0];
       const stringOfUsersAccount = new String(usersAccount)
       console.log(stringOfUsersAccount.slice(0, 4)); // "01"
       setFirstPartAddress(stringOfUsersAccount.slice(0, 5));
       setSecondPartAddress('____'+stringOfUsersAccount.slice(37, 42));

     }
     init()
   });

  const connectWallet = async(e) => {
    await wallet.connect()
    const usersAccount = props.accounts;
    const stringOfUsersAccount = new String(usersAccount)
    console.log(stringOfUsersAccount.slice(0, 4)); // "01"
    setFirstPartAddress('Welcome');
    setSecondPartAddress('');
    window.location.reload();

  }

  return(
    <div>

      <ButtonGroup className="mb-2">
          <Button >Network</Button>


          <Button onClick={connectWallet} id="wallet-button" className="customWalletButton " >{firstPartAddress}{secondPartAddress}</Button>

        </ButtonGroup>

        <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal className="Networkcustom modal-dialog" data-backdrop="false" show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Choose Network</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>




        <br />

    </div>
  )
}

// Wrap everything in <UseWalletProvider />
export default (props) => (
  <UseWalletProvider

    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}>

    <Connection accounts={props.accounts}/>

  </UseWalletProvider>
)
