import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';
import MyNav from './MyNav';
import MyCards from './components/Structure/Game1/Cards';
import SingleCard from './components/Structure/Game1/SingleCard';
import moment from 'moment';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component, useState, useEffect } from 'react'
import Connection from "./Connection";
import { useWallet, UseWalletProvider } from 'use-wallet'
import "./App.css";



import Timer from './components/Structure/Game1/Timer';
import HomeCards from './components/Structure/Game1/HomeCards';
import HomeHuntCards from './components/Structure/Game1/HomeHuntCards';
import Cards from './components/Structure/Game1/Cards';
import PageCard from './components/Structure/Game1/PageCard';
import HuntWowCards from './components/Structure/Game1/HuntWowCards';
import EnterHuntCards from './components/Structure/Game1/EnterHuntCards';

import Confetti from 'react-confetti'

// Hook
function useWindowSize() {
  // Initialize state with undefined width/height so server and client renders match
  // Learn more here: https://joshwcomeau.com/react/the-perils-of-rehydration/
  const [windowSize, setWindowSize] = useState({
    width: undefined,
    height: undefined,
  });

  useEffect(() => {
    // Handler to call on window resize
    function handleResize() {
      // Set window width/height to state
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    // Add event listener
    window.addEventListener("resize", handleResize);
    // Call handler right away so state gets updated with initial window size
    handleResize();
    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleResize);
  }, []); // Empty array ensures that effect is only run on mount
  return windowSize;
}

const Hunt1 = (props) => {

  const commaNumber = require('comma-number')
  const { width, height } = useWindowSize();

  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_time_lock_cost = props.game1_time_lock_cost;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;
  const allGame1_deadline_time= props.allGame1_deadline_time;
  const allGame1_username = props.allGame1_username;
  const allGame1_total_game_tries = props.allGame1_total_game_tries;

  const renderTooltip = (props) => (

  <Tooltip id="button-tooltip" {...props}>
    Once you have purchased your Blox they buy you 30 seconds to enter your answer, no one else can answer in this time and this stops bots from beating you to it!
  </Tooltip>
);

  const winner = props.treasure_found;

    return (

      <div className="spaceTopHome">
      {winner? (
        <div><Confetti
          width={width}
          height={height}
	        numberOfPieces={300}

          drawShape={ctx => {
            ctx.beginPath();
            for (var i = 0; i < 6; i++) {
              ctx.lineTo(10 + 20 * Math.cos(2 * Math.PI / 6 * i), 10 + 20 * Math.sin(2 * Math.PI / 6 * i));
            }
            ctx.closePath();
            ctx.stroke();
          }}
        />

        </div>

      ):(
        <div>

        </div>
      )}

      <div>
            <div className="siteTitle">Welcome, Elon has lost his Rocket! Can you help him find it to win the prize?</div>
      <SingleCard{...props}/>

      <Container className='mt-5' fluid="md">
            <CardGroup className='mt-5'>
              <CardColumns>

                <EnterHuntCards {...props}/>
                <br/>
                <HuntWowCards {...props}/>

                <PageCard {...props}/>

                <HomeCards/>

              </CardColumns>
            </CardGroup>
        </Container>

      </div>



</div>



    );

}

export default Hunt1;
