      const diffInMs = Math.abs(date2 - date1);
      return diffInMs / (1000 * 60);
      }

    function getDifferenceInSeconds(date1, date2) {
      const diffInMs = Math.abs(date2 - date1);
      return diffInMs / 1000;
      }


