import React, { Component, useState, useEffect } from 'react'

import { CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown,Row,Col } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './Card.css';

const Timer = (props) => {


// Timer
  let currentTimestamp = Date.now()
  // console.log(currentTimestamp); // get current timestamp
//   let date = new Intl.DateTimeFormat('en-US', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(currentTimestamp)
  // console.log(currentTimestamp);
  const deadline = props.time;
  const mytime = deadline - currentTimestamp;


  // console.log("Deadline:", mytime)


  const [count, setTime] = useState(mytime);



  useEffect(() => {
    const timer = window.setInterval(() => {
      setTime(prevTime => prevTime - 1); // <-- Change this line!
    }, 1000);
    return () => {
      window.clearInterval(timer);
    };


  }, []);

  // if (count <= 0){
  //   count = 0;
  // }




  return(

    <div>

      <div className="attemptPrize">This time slot will expire in {count} seconds!</div>

    </div>

  );

};

export default Timer;
