import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import MyNav from './components/Structure/Nav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component } from 'react'
import Web3 from 'web3'



// next we'll create a new instance of web3.
var web3 = new Web3();

// Set the provider
web3.setProvider(new Web3.providers.WebsocketProvider('ws://localhost:8545')
);

const abi = require('./TreasureBlox.abi.json');
const mepAddress = '0xd9145CCE52D386f254917e481eB44e9943F39138'

let contract = new web3.eth.Contract(abi,mepAddress);


function checkBalance() {  web3.eth.personal.getAccounts().then(accounts => {console.log('accounts', accounts);

    web3.eth.getBalance(accounts[0]).then(balance => {console.log('balance[0]', balance);

    });
  });
}

class App extends Component {
  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    console.log("Accounts: ",accounts)

  }

  constructor(props) {
    super(props)
    this.state = { account: '' };
    this.state = {isToggleOn: true};

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn
    }));
  }

















  render() {

    return (
        <div className="background customFont">

          <MyNav />
          <SingleCard/>

          <Button onClick={this.handleClick}>
                  {this.state.isToggleOn ? 'ON' : 'OFF'}
                </Button>

          <MyCards/>
          <MyCards/>
      </div>
    );
  }
}

export default App;
