import React, {useState} from 'react';

import img1 from '../images/Treasure-hunt.jpeg';


import { Modal,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown,Row,Col } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './SingleCard.css';

const SingleCard = (props) => {
  const commaNumber = require('comma-number')


  const decimals = props.decimals;


  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_entry_cost = commaNumber(props.game1_entry_cost,',');



  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  return(

    <Container className='mt-5' fluid="md">

    <Row >

    <Col>
    <div>
      <Card className="customRoundedOne">
      <Card.Header >Prize</Card.Header>
      <Card.Body className='customBodyTwo'>
        <div className="gameMainTitle">Total Treasure for Elon's Rocket</div>

        <div className="gamePrize">Prize {game1_prize} Blox</div>
        <div className="postMainPrize">Entry Cost {game1_entry_cost} Blox</div>
        <br/>

      </Card.Body >

      </Card>
    </div>

    </Col>
    <Col>
    <div >
      <Card className="customRoundedOne">
      <Card.Header >Riddle</Card.Header>
      <Card.Body className='customBodyTwo'>
        <div className="gameMainTitle">{props.game1_riddle}</div>

        <div className="gamePrize"><p variant="primary" onClick={handleShow}>{props.game1_clue}</p></div>
        <div className="postMainPrize">Best not go to Hawaii</div>
        <br/>



        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>WORMHOLE ACTIVATED!</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <blockquote class="tiktok-embed" cite="https://www.tiktok.com/@elonxmusk/video/6939914658610449670" data-video-id="6939914658610449670" style="max-width: 605px;min-width: 325px;" > <section> <a target="_blank" title="@elonxmusk" href="https://www.tiktok.com/@elonxmusk">@elonxmusk</a> <p>Elon Family History <a title="spacex" target="_blank" href="https://www.tiktok.com/tag/spacex">##spacex</a> <a title="elonmusk" target="_blank" href="https://www.tiktok.com/tag/elonmusk">##elonmusk</a> <a title="space" target="_blank" href="https://www.tiktok.com/tag/space">##space</a> <a title="cybertruck" target="_blank" href="https://www.tiktok.com/tag/cybertruck">##cybertruck</a> <a title="nasa" target="_blank" href="https://www.tiktok.com/tag/nasa">##nasa</a> <a title="satellite" target="_blank" href="https://www.tiktok.com/tag/satellite">##satellite</a> <a title="universe" target="_blank" href="https://www.tiktok.com/tag/universe">##universe</a> <a title="cars" target="_blank" href="https://www.tiktok.com/tag/cars">##cars</a> <a title="tesla" target="_blank" href="https://www.tiktok.com/tag/tesla">##tesla</a> <a title="truck" target="_blank" href="https://www.tiktok.com/tag/truck">##truck</a> <a title="fyp" target="_blank" href="https://www.tiktok.com/tag/fyp">##fyp</a> <a title="tunnel" target="_blank" href="https://www.tiktok.com/tag/tunnel">##tunnel</a> <a title="tech" target="_blank" href="https://www.tiktok.com/tag/tech">##tech</a> <a title="engineering" target="_blank" href="https://www.tiktok.com/tag/engineering">##engineering</a> <a title="inspiring" target="_blank" href="https://www.tiktok.com/tag/inspiring">##inspiring</a></p> <a target="_blank" title="♬ original sound - Elon Musk SpaceX Tesla" href="https://www.tiktok.com/music/original-sound-6939914494181116677">♬ original sound - Elon Musk SpaceX Tesla</a> </section> </blockquote> <script async src="https://www.tiktok.com/embed.js"></script>

          </Modal.Body>

        </Modal>



      </Card.Body >

      </Card>
    </div>

    </Col>
  </Row>





    </Container>


  );


};

export default SingleCard;
