// App.js

import React, {useState, useEffect} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";
import getWeb3 from "./getWeb3";


import WalletConnectProvider, { useWalletConnect } from "react-native-walletconnect";



const Connection = (props) =>  {



  const wallet = useWallet()
  const [firstPartAddress, setFirstPartAddress] = useState('Connect Wallet');
  const [secondPartAddress, setSecondPartAddress] = useState('');

  useEffect(() => {

     const init = async() => {
       const web3 = await getWeb3();
       const accounts = await web3.eth.getAccounts();
       const usersAccount = accounts[0];
       const stringOfUsersAccount = new String(usersAccount)
       console.log(stringOfUsersAccount.slice(0, 4)); // "01"
       setFirstPartAddress(stringOfUsersAccount.slice(0, 5));
       setSecondPartAddress('____'+stringOfUsersAccount.slice(37, 42));

     }
     init()
   });

  const connectWallet = async(e) => {
    await wallet.connect("walletconnect")
    const usersAccount = props.accounts;
    const stringOfUsersAccount = new String(usersAccount)
    console.log(stringOfUsersAccount.slice(0, 4)); // "01"
    setFirstPartAddress('Welcome');
    setSecondPartAddress('');
    window.location.reload();

  }


  const {
    createSession,
    killSession,
    session,
    signTransaction,
  } = useWalletConnect();
  const hasWallet = !!session.length;

  return(
    <div>
      <Button onClick={connectWallet} id="wallet-button" className="customButton enableEthereumButton">{firstPartAddress}{secondPartAddress}</Button>
      <>
            {!hasWallet && (
              <Button title="Connect" onPress={createSession} />
            )}
            {!!hasWallet && (
              <Button
                title="Sign Transaction"
                onPress={() => signTransaction({
                  from: "0xbc28Ea04101F03aA7a94C1379bc3AB32E65e62d3",
                  to: "0x89D24A7b4cCB1b6fAA2625Fe562bDd9A23260359",
                  data: "0x",
                  gasPrice: "0x02540be400",
                  gas: "0x9c40",
                  value: "0x00",
                  nonce: "0x0114",
                })}
              />
            )}
            {!!hasWallet && (
              <Button
                title="Disconnect"
                onPress={killSession}
              />
            )}
          </>
    </div>
  )
}

// Wrap everything in <UseWalletProvider />
export default (props) => (
  <UseWalletProvider

    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}>




    <Connection accounts={props.accounts}/>


  </UseWalletProvider>
)
