// App.js

import React, {useState} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';


import MyNav from "./Nav";
import App from "./App";




function Connection(props) {

  const wallet = useWallet()
  const [account,setAccountName] = useState(false)

  // if (props.accounts){
  //   setAccountName(true)
  // }

    console.log("this here",props.accounts)




  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()
    setAccountName(true)

  }



  return account?(
    <div>
    <Button onClick={connectWallet} className="customButton enableEthereumButton">Fucking Connected</Button>

    </div>
  ):(

    <div>
    <Button onClick={connectWallet} className="customButton enableEthereumButton">Connect Wallet</Button>
    </div>

  )

}


// Wrap everything in <UseWalletProvider />
export default () => (
  <UseWalletProvider
    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}

  >
    <Connection />
  </UseWalletProvider>
)
