        const canvas = document.getElementById("canvas");
        const ctx = canvas.getContext("2d");


        function drawGrip() {
            //Draw playerRect first
            ctx.fillStyle = "#000000";
            ctx.fillRect(250,350,100,300);
        }

        function drawSight() {
            //Draw playerRect first
            ctx.fillStyle = "#000000";
            ctx.fillRect(298,230,4,100);
        }

        function drawTrigger() {
            //Draw playerRect first
            ctx.fillStyle = "#231709";
            ctx.fillRect(285,350,30,200);

        }



        function drawChamber(){
          ctx.fillStyle = "#000000";
          ctx.beginPath();
          ctx.arc(300, 350, 100, 0, 2 * Math.PI);
          ctx.fill();
        }

        function drawChamberRim(){
          ctx.fillStyle = "#696969";

          ctx.beginPath();
          ctx.arc(300, 350, 70, 0, 2 * Math.PI);
          ctx.stroke();
          ctx.fill();

        }

        function drawInnerChamber(){
          ctx.fillStyle = "#000000";
          ctx.beginPath();
          ctx.arc(300, 350, 65, 0, 2 * Math.PI);
          ctx.fill();
        }



        function drawBarrel(){

          ctx.fillStyle = "#000000";
          ctx.beginPath();
          ctx.arc(300, 290, 50, 0, 2 * Math.PI);

          ctx.fill();

        }
        function drawBarrelInner(){

          // if statement

          ctx.fillStyle = "#080808";

          ctx.beginPath();
          ctx.arc(300, 290, 20, 0, 2 * Math.PI);
          ctx.stroke();
          ctx.fill();

        }

        function drawBullets(){

          // ctx.fillRect(0, -12, 40, 24); // Shadow
          bullets.forEach(bullet => {

              ctx.fillStyle = bullet.color;
              ctx.beginPath();
              ctx.arc(bullet.x, bullet.y, 20, 0, 2 * Math.PI);
              ctx.fill();

              // ctx.fillRect(bullet.x,bullet.y,bullet.width,bullet.height);
          })
        }


        // on click
        ctx.save();
        drawGrip();
        drawSight();
        drawTrigger();
        drawChamber();
        drawChamberRim();
        drawInnerChamber();
        drawBarrel();
        drawBarrelInner();
        ctx.restore();

        drawBullets();


        const rate = 0.2; // Number of rotations per second

        function drawCircles(angle) {

            var i = 0;
            const numElements = 6;
            const step = (2 * Math.PI) / numElements;



            ctx.beginPath();
            while (i < numElements) {
                 const x = ctx.canvas.width / 2 + 100 * Math.cos(angle + i * step);
                 const y = ctx.canvas.height / 2 + 100 * Math.sin(angle + i * step);
                 ctx.moveTo(x + 10, y);
                 ctx.arc(x, y, 20, 0, 2 * Math.PI);
                 i++;
            }
            ctx.fillStyle = "#000000";
            ctx.fill();
        }


        requestAnimationFrame(renderLoop);  // rAF to start animation

        function renderLoop(time) {  // rAF callback
            ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

            drawCircles(((time * Math.PI * 2) / 1000) * rate);

            requestAnimationFrame(renderLoop);  // request next frame
        }








