      {props.playLevel1Game1?(
        <div>
        <Redirect to="/hunt1" {...props}/>
        </div>
      ):(
        <div>
        {play?(
          <div style={{height: height}}>
          <Screen {...props}/>
          <ReactAudioPlayer
            src={bloxEscape}
            autoPlay
          />
          </div>
        ):(
          <div style={{height: height}}>
          <ReactAudioPlayer
            src={gameSound}
            autoPlay
          />
          <ReactAudioPlayer
            src={help}
            autoPlay
          />

          {option1?(
            <div>

