// App.js

import React, {useState} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";


const Connection = (props) =>  {

  const wallet = useWallet()

  const [account,setAccountName] = useState(false)


  // if (props.accounts){
  //   setAccountName(true)
  // }

  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()


    // setAccountName(true)

  }



  return account?(
    <div>
      <Button onClick={() => wallet.connect()} className="customButton enableEthereumButton">{props.accounts}WTF</Button>
    </div>
  ) :(
    <div>
      <Button onClick={() => wallet.connect()} className="customButton enableEthereumButton">{props.accounts}WTF2</Button>

    </div>
  )

}


// Wrap everything in <UseWalletProvider />
export default () => (
  <UseWalletProvider
    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}>


    <Connection />
  </UseWalletProvider>
)
