import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';
import MyNav from './MyNav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component, useState, useEffect } from 'react'
import getWeb3 from "./getWeb3";
import Connection from "./Connection";
import { useWallet, UseWalletProvider } from 'use-wallet'
import "./App.css";

const App = () => {
  const [web3,setWeb3] = useState(null)
  const [accounts,setAccounts] = useState(null)
  const [contract,setContract] = useState(null)
  const [game0,setGame0] = useState('Pending')
  const [game1,setGame1] = useState('Pending')
  const [game2,setGame2] = useState('Pending')

  useEffect(() => {

      const init = async() => {

        const web3 = await getWeb3();

        const accounts = await web3.eth.getAccounts();

        const abi = require('./TreasureBlox.abi.json');
        // Set the provider
        // web3.setProvider(new web3.providers.WebsocketProvider('ws://localhost:8545'));
        // this.web3.setProvider(new this.web3.providers.HttpProvider('https://data-seed-prebsc-1-s1.binance.org:8545/'));
        const contract = new web3.eth.Contract(abi,"https://data-seed-prebsc-1-s1.binance.org:8545/" && "0xc4c51a5cac72b26d6467130490f72950610f40ed");


        const game0 = await contract.methods.Games(0).call();
        setGame0(game0)
        console.log("game 0: ", game0)

        const game1 = await contract.methods.Games(1).call();
        setGame1(game1)

        const game2 = await contract.methods.Games(2).call();
        setGame2(game2)

        setWeb3(web3)
        setAccounts(accounts[0])
        setContract(contract)


      }
      init()


    },[accounts])





// const { web3, accounts, contract } = this.state;



    return (

        <div className="background customFont">

          <MyNav accounts={accounts}/>

          <SingleCard/>





          <MyCards game0={game0['live']} game1={game1} game2={game2} />
          <MyCards/>

      </div>
    );

}

export default App;
