import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';
import MyNav from './MyNav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';
import moment from 'moment';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component, useState, useEffect } from 'react'
import Connection from "./Connection";
import { useWallet, UseWalletProvider } from 'use-wallet'
import "./App.css";

import Timer from './components/Structure/Timer';
import HomeCards from './components/Structure/HomeCards';
import HomeHuntCards from './components/Structure/HomeHuntCards';
import Cards from './components/Structure/Cards';
import PageCard from './components/Structure/PageCard';
import HuntWowCards from './components/Structure/HuntWowCards';
import EnterHuntCards from './components/Structure/EnterHuntCards';




const Hunt1 = (props) => {


  const commaNumber = require('comma-number')


  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_time_lock_cost = props.game1_time_lock_cost;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;
  const allGame1_deadline_time= props.allGame1_deadline_time;
  const allGame1_username = props.allGame1_username;
  const allGame1_total_game_tries = props.allGame1_total_game_tries;



  // console.log(game1_id)s

  const renderTooltip = (props) => (


  <Tooltip id="button-tooltip" {...props}>
    Once you have purchased your Blox they buy you 30 seconds to enter your answer, no one else can answer in this time and this stops bots from beating you to it!
  </Tooltip>
);






    return (

      <div className="spaceTopHome">
      <div>
            <div className="siteTitle">Welcome, Elon has lost his Rocket! Can you help him find it to win the prize?</div>
      <SingleCard
      game1_id={props.game1_id}
      game1_live={props.game1_live}
      game1_prize={props.game1_prize}
      game1_question_hash={props.game1_question_hash}
      game1_time_lock_cost={props.game1_time_lock_cost}
      game1_submit_secret_cost={props.game1_submit_secret_cost}
      game1_riddle={props.game1_riddle}
      game1_clue={props.game1_clue}
      game1_head_start_time={props.game1_head_start_time}
      game1_entry_limit={props.game1_entry_limit}
      game1_entry_cost={props.game1_entry_cost}
      allGame1_id={props.allGame1_id}
      allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
      allGame1_deadline_time={props.allGame1_deadline_time}
      allGame1_username={props.allGame1_username}
      allGame1_total_game_tries={props.allGame1_total_game_tries}
      countGame1={props.countGame1}
      countGame1DeadlineTrue={props.countGame1DeadlineTrue}
      decimals={props.decimals}
      totalTreasure={props.totalTreasure}
      contract={props.contract}
      accounts={props.accounts}
      game1numberOfEntries={props.game1numberOfEntries}
      userGame1_id={props.userGame1_id}
      userEntered_game1={props.userEntered_game1}
      userGame1_headstart_time={props.userGame1_headstart_time}
      userGame1_live={props.userGame1_live}/>


      <Container className='mt-5' fluid="md">
            <CardGroup className='mt-5'>
              <CardColumns >

              <EnterHuntCards
              web3={props.web3}
              game1_id={props.game1_id}
              game1_live={props.game1_live}
              game1_prize={props.game1_prize}
              game1_question_hash={props.game1_question_hash}
              game1_time_lock_cost={props.game1_time_lock_cost}
              game1_submit_secret_cost={props.game1_submit_secret_cost}
              game1_riddle={props.game1_riddle}
              game1_clue={props.game1_clue}
              game1_head_start_time={props.game1_head_start_time}
              game1_entry_limit={props.game1_entry_limit}
              game1_entry_cost={props.game1_entry_cost}
              allGame1_id={props.allGame1_id}
              allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
              allGame1_deadline_time={props.allGame1_deadline_time}
              allGame1_username={props.allGame1_username}
              allGame1_total_game_tries={props.allGame1_total_game_tries}
              countGame1={props.countGame1}
              countGame1DeadlineTrue={props.countGame1DeadlineTrue}
              decimals={props.decimals}
              totalTreasure={props.totalTreasure}
              contract={props.contract}
              accounts={props.accounts}
              game1numberOfEntries={props.game1numberOfEntries}
              userGame1_id={props.userGame1_id}
              userEntered_game1={props.userEntered_game1}
              userGame1_headstart_time={props.userGame1_headstart_time}
              userGame1_live={props.userGame1_live}/>
              <br/>

              <HuntWowCards
              game1_id={props.game1_id}
              game1_live={props.game1_live}
              game1_prize={props.game1_prize}
              game1_question_hash={props.game1_question_hash}
              game1_time_lock_cost={props.game1_time_lock_cost}
              game1_submit_secret_cost={props.game1_submit_secret_cost}
              game1_riddle={props.game1_riddle}
              game1_clue={props.game1_clue}
              game1_head_start_time={props.game1_head_start_time}
              game1_entry_limit={props.game1_entry_limit}
              game1_entry_cost={props.game1_entry_cost}
              allGame1_id={props.allGame1_id}
              allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
              allGame1_deadline_time={props.allGame1_deadline_time}
              allGame1_username={props.allGame1_username}
              allGame1_total_game_tries={props.allGame1_total_game_tries}
              countGame1={props.countGame1}
              countGame1DeadlineTrue={props.countGame1DeadlineTrue}
              decimals={props.decimals}
              totalTreasure={props.totalTreasure}
              game1numberOfEntries={props.game1numberOfEntries}/>

              <PageCard
              game1_id={props.game1_id}
              game1_live={props.game1_live}
              game1_prize={props.game1_prize}
              game1_question_hash={props.game1_question_hash}
              game1_time_lock_cost={props.game1_time_lock_cost}
              game1_submit_secret_cost={props.game1_submit_secret_cost}
              game1_riddle={props.game1_riddle}
              game1_clue={props.game1_clue}
              game1_head_start_time={props.game1_head_start_time}
              game1_entry_limit={props.game1_entry_limit}
              game1_entry_cost={props.game1_entry_cost}
              allGame1_id={props.allGame1_id}
              allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
              allGame1_deadline_time={props.allGame1_deadline_time}
              allGame1_username={props.allGame1_username}
              allGame1_total_game_tries={props.allGame1_total_game_tries}
              countGame1={props.countGame1}
              countGame1DeadlineTrue={props.countGame1DeadlineTrue}
              decimals={props.decimals}
              totalTreasure={props.totalTreasure}
              game1numberOfEntries={props.game1numberOfEntries}/>

              <HomeCards/>

              </CardColumns>
            </CardGroup>
        </Container>

      </div>



</div>



    );

}

export default Hunt1;
