    var groundHeight = size - 20;
    var color = "hsl(180, 80%, 80%)";
    var roughness = 2;
    var maxDifference = size / 5;

    ctx.globalCompositeOperation = "lighter";

    ctx.strokeStyle = color;
    ctx.shadowColor = color;

    ctx.fillStyle = color;
    ctx.fillRect(0, 0, size, size);
    ctx.fillStyle = "hsla(0, 0%, 10%, 0.2)";



    function createLightning() {
      var segmentHeight = groundHeight - center.y;
      var lightning = [];
      lightning.push({x: center.x, y: center.y});
      lightning.push({x: Math.random() * (size - 100) + 50, y: groundHeight + (Math.random() - 0.9) * 50});
      var currDiff = maxDifference;
      while (segmentHeight > minSegmentHeight) {
        var newSegments = [];
        for (var i = 0; i < lightning.length - 1; i++) {
          var start = lightning[i];
          var end = lightning[i + 1];
          var midX = (start.x + end.x) / 2;
          var newX = midX + (Math.random() * 2 - 1) * currDiff;
          newSegments.push(start, {x: newX, y: (start.y + end.y) / 2});
        }

        newSegments.push(lightning.pop());
        lightning = newSegments;

        currDiff /= roughness;
        segmentHeight /= 2;
      }
      return lightning;
    }




  return (
    <div id="top">




    {isGameOver && <ReactAudioPlayer
      src={ohno}
      autoPlay
    />}


    {exit?(
      <div>

      </div>
    ):(
      <div>

      <Redirect to="/TreasureBloxPartner/:top" {...props}/>

      </div>
    )}

    {redirectLevel1More?(
      <div>
      <Redirect to="/PartnershipLevel1More/:top" {...props}/>
      </div>
    ):(
      <div>

      </div>

    )}



<center>
{props.is_meter?(<div className="wording" style={{color:"white"}}>Force Field {timeleft}</div>):(<div className="wording">Fighter Pilot {timeleft}</div>)}
<br/>

<canvas width="600" height="600" id="canvas"> </canvas>

</center>
<br/>
    </div>
  );
}

