import React, {useState, useEffect, useRef} from "react";
// import Bird from './components/Bird'
// import Obstacles from './components/Obstacles'
import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';
import useSound from 'use-sound';
//
// import lazer from './assets/Lazer.wav';
// import ohno from './assets/troy_oh_no.mp4';
import './style.css';


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

import ReactAudioPlayer from 'react-audio-player';


// Hook
function useWindowSize() {
  // Initialize state with undefined width/height so server and client renders match
  // Learn more here: https://joshwcomeau.com/react/the-perils-of-rehydration/
  const [windowSize, setWindowSize] = useState({
    width: undefined,
    height: undefined,
  });

  useEffect(() => {
    // Handler to call on window resize
    function handleResize() {
      // Set window width/height to state
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    // Add event listener
    window.addEventListener("resize", handleResize);
    // Call handler right away so state gets updated with initial window size
    handleResize();
    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleResize);
  }, []); // Empty array ensures that effect is only run on mount
  return windowSize;
}




const Screen = (props) =>{

  const { width, height } = useWindowSize();


  useEffect(() => {



      const canvas = document.getElementById("canvas");
      const ctx = canvas.getContext("2d");

      // Global Varabales

      let controlPlayer = false;
      let hasGameStarted = false;


      // Prevent game from working whilst page reloads
      let hasGameOver = false;
      let startDate = null;


      function gameOver(){
        if(!hasGameOver){
          let endDate = new Date();
          let timeDiff = endDate - startDate;
          if (timeDiff < 60000){
            alert('You survived ${timeDiff/1000} seconds!');
          }

          hasGameOver = true;
          location.reload();
        }
      }


      // Canvas black boarder

      function drawBoarder(){
        ctx.fillStyle = "black";
        ctx.fillRect(0,0,canvas.width,canvas.height);
        ctx.clearRect(50,50,500,500);
      }

      let playerRect = {
        x:275,
        y:275,
        width:50,
        height:50,
      }

      // Game Elements
      let rectangles = [
        {
          x:75,
          y:75,
          dx:5,
          dy:4,
          width:75,
          height:75,
          color:"#000099",
        },
        {
          x:400,
          y:75,
          dx:-5,
          dy:5.5,
          width:80,
          height:60,
          color:"#000099",
        },
        {
          x:75,
          y:445,
          dx:5,
          dy:-5,
          width:40,
          height:80,
          color:"#000099",
        },
        {
          x:420,
          y:450,
          dx:-5,
          dy:-5,
          width:130,
          height:25,
          color:"#000099",
        },
      ]

      // Render rectangles to canvas

      function drawRect(){
        // Draw player rect first
        ctx.fillStyle = "#901234";
        ctx.fillRect(playerRect.x,playerRect.y,playerRect.width,playerRect.height);

        rectangles.forEach(rect => {
          ctx.fillStyle = rect.color;
          ctx.fillRect(rect.x,rect.y,rect.width,rect.height);
        });

      }

      // collision
      function playerCollisionDetection(){
        if(playerRect.x + playerRect.width > 550 || playerRect.x < 50 || playerRect.y + playerRect.height > 550 || playerRect.y < 50
        ){
          gameOver();
        }
      }

      function update(){
        ctx.clearRect(0,0,canvas.width,canvas.height);

        drawBoarder();
        drawRect();

        playerCollisionDetection();

        requestAnimationFrame(update);
      }

      // call update methord on initial document load
      update();


      // Returns true if collision
      function isCursorInRect(x,y,rect){
        return(x>rect.x && x<rect.x+rect.width && y>rect.y && y<rect.y+rect.height);
      }

      canvas.addEventListener("mousedown", e => {

        // get x & y coordinates in relation to the canvas
        const pos = {
          x: e.clientX - canvas.offsetLeft,
          y: e.clientY - canvas.offsetTop,
        };

        // See if they clicked on red square
        if (isCursorInRect(pos.x,pos.y,playerRect)){

          //
          if(!hasGameStarted){
            startDate = new Date();
          }
          hasGameStarted = true;
          controlPlayer = true;
        }

      })

      canvas.addEventListener("mousemove", e => {

        if(controlPlayer && !hasGameOver){
          // get x & y coordinates in relation to the canvas
          const pos = {
            x: e.clientX - canvas.offsetLeft,
            y: e.clientY - canvas.offsetTop,
          };
          playerRect.x = pos.x - 25;
          playerRect.y = pos.y - 25;
        }

      })



      canvas.addEventListener("mouseup", () =>{
        controlPlayer = false;
      })

    }, []);





  return (
    <div >

<center>
Fighter Pilot<br/>
<canvas width="600" height="600" id="canvas"> </canvas>

</center>
<br/>
    </div>
  );
}

export default Screen;
