import React, {useState} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'

import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Nav.css';
import Connection from "./Connection";

const MyNav = (props) => {

  const account = props.accounts

  const wallet = useWallet()
  // if (props.accounts){
  //   setAccountName(true)
  // }

  console.log("this here",props.accounts)


  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()
    // setAccountName(true)

  }



  return(

    <Navbar collapseOnSelect className="fixed-top MyBakground" expand="lg" variant="dark">
      <Container>
      <Navbar.Brand href="#home">In Nav.js</Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#features">{props.accounts}</Nav.Link>
          <Nav.Link href="#pricing">Pricing</Nav.Link>
          <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
          </NavDropdown>
        </Nav>
        <Nav className="ml-auto">
          <Nav.Link href="#deets">More deets</Nav.Link>
          <Nav.Link eventKey={2} href="#memes">
            Dank memes
          </Nav.Link>
          // <Connection accounts={props.accounts}/>
          <div>

          <Button onClick={connectWallet} className="customButton enableEthereumButton">{props.accounts}Fucking Connected</Button>

          </div>





        </Nav>
      </Navbar.Collapse>
      </Container>
    </Navbar>


  );


};

export default MyNav(<UseWalletProvider
  chainId={1337}
  connectors={{
    // This is how connectors get configured
    provided: {provider: window.cleanEthereum},
  }}

>
  <Connection />
</UseWalletProvider>);
