import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import reportWebVitals from './reportWebVitals';
import 'bootstrap/dist/css/bootstrap.min.css';


//
//
// ReactDOM.render(
//   <React.StrictMode>
//     <App />
//   </React.StrictMode>,
//   document.getElementById('root')
// );
//
// // If you want to start measuring performance in your app, pass a function
// // to log results (for example: reportWebVitals(console.log))
// // or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
// reportWebVitals();




import {
  ConnectionRejectedError,
  UseWalletProvider,
  useWallet,
} from 'use-wallet'

function App() {
  const wallet = useWallet()
  const blockNumber = wallet.getBlockNumber()
  const activate = (connector) => wallet.connect(connector)

  return (
    <>
      <h1>use-wallet</h1>

      {(() => {
        if (wallet.error?.name) {
          return (
            <p>
              <span>
                {wallet.error instanceof ConnectionRejectedError
                  ? 'Connection error: the user rejected the activation'
                  : wallet.error.name}
              </span>
              <button onClick={() => wallet.reset()}>retry</button>
            </p>
          )
        }

        if (wallet.status === 'connecting') {
          return (
            <p>
              <span>Connecting to {wallet.connector}â€¦</span>
              <button onClick={() => wallet.reset()}>cancel</button>
            </p>
          )
        }

        if (wallet.status === 'connected') {
          return (
            <p>
              <span>Connected.</span>
              <button onClick={() => wallet.reset()}>disconnect</button>
            </p>
          )
        }

        return (
          <div className="connect">
            <div className="connect-label">Connect:</div>
            <div className="connect-buttons">
              <button onClick={() => activate('injected')}>injected</button>
              <button onClick={() => activate('frame')}>frame</button>
              <button onClick={() => activate('portis')}>portis</button>
              <button onClick={() => activate('fortmatic')}>fortmatic</button>
              <button onClick={() => activate('torus')}>torus</button>
              <button onClick={() => activate('walletconnect')}>
                walletconnect
              </button>
              <button onClick={() => activate('walletlink')}>walletlink</button>
            </div>
          </div>
        )
      })()}

      {wallet.status === 'connected' && (
        <>
          <p>
            <span>Account:</span>
            <span>{wallet.account}</span>
          </p>
        </>
      )}

      {wallet.account && (
        <p>
          <span>Balance:</span>
          <span>

          </span>
        </p>
      )}

      {wallet.status === 'connected' && (
        <p>
          <span>Block:</span> <span>{blockNumber || 'â€¦'}</span>
        </p>
      )}
    </>
  )
}

ReactDOM.render(
  <UseWalletProvider
    autoConnect

    connectors={{
      injected: {
        //allows you to connect and switch between mainnet and rinkeby within Metamask.
        chainId: [1, 4],
      },
      fortmatic: {
        chainId: [1],
        apiKey: '',
      },
      portis: {
        dAppId: '',
        chainId: [1],
      },
      walletconnect: {
        rpc: {
          1: 'https://mainnet.infura.io/v3/a0d8c94ba9a946daa5ee149e52fa5ff1',
          4: 'https://rinkeby.infura.io/v3/a0d8c94ba9a946daa5ee149e52fa5ff1',
        },
        bridge: 'https://bridge.walletconnect.org',
        pollingInterval: 12000,
      },
      walletlink: {
        chainId: [1],
        url: 'https://mainnet.eth.aragon.network/',
      },
      bsc: {
        chainId: [1337],
        url: {provider: window.cleanEthereum},
      },
    }}
  >
    <App />
    <style>
      {`
        body {
          width: 40rem;
          margin: 0 auto;
          font-family: sans-serif;
          font-size: 18px;
          line-height: 1.5;
        }
        button {
          width: 6rem;
        }
        h1 {
          font-weight: 400;
        }
        p {
          display: grid;
          justify-content: space-between;
          align-items: center;
          grid-auto-flow: column;
          gap: 1rem;
          align-items: center;
          margin: 2rem 0;
        }
        button {
          height: 3rem;
          cursor: pointer;
          font-size: 1rem;
          padding: 0;
        }
        .connect-label {
          margin-bottom: 1rem;
        }
        .connect-buttons {
          display: grid;
          gap: 10px;
          grid-template-columns: repeat(auto-fill, minmax(140px, 1fr));
        }
        .connect-buttons button {
          width: 100%;
          height: 4rem;
        }
      `}
    </style>
  </UseWalletProvider>,
  document.querySelector('#root')
)
