// App.js

import React, {useState} from 'react'
import { useWallet, UseWalletProvider } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";


const Connection = (props) =>  {

  const wallet = useWallet()

  const [account,setAccountName] = useState(false)


  // if (props.accounts){
  //   setAccountName(true)
  // }


  console.log("this here",props.accounts)

  const connectWallet = async(e) => {
    e.preventDefault()
    await wallet.connect()
    // setAccountName(true)
  }




  return (


    <div>
    {connectWallet.status === 'connected' ? (
        <div>
          <div>Account: {connectWallet.wallet.account}</div>
          <div>Balance: {connectWallet.wallet.balance}</div>
          <button onClick={() => wallet.reset()}>disconnect</button>
        </div>
      ) : (
        <div>

          <Button onClick={connectWallet} className="customButton enableEthereumButton">{props.balance}WTF</Button>


        </div>
      )}
    </div>

  )

}


// Wrap everything in <UseWalletProvider />
export default () => (
  <UseWalletProvider
    chainId={1337}
    connectors={{
      // This is how connectors get configured
      provided: {provider: window.cleanEthereum},
    }}

  >
    <Connection />
  </UseWalletProvider>
)
