// App.js

import React, {useState, useEffect} from 'react'
import { useWallet, UseWalletProvider,ConnectionRejectedError } from 'use-wallet'
import { Button } from 'react-bootstrap';

import MyNav from "./MyNav";
import App from "./App";
import getWeb3 from "./getWeb3";


import TokenAmount from 'token-amount'


const Connection = (props) =>  {



  const wallet = useWallet()
  const blockNumber = wallet.getBlockNumber()
  const activate = (connector) => wallet.connect(connector)

  const [firstPartAddress, setFirstPartAddress] = useState('Connect Wallet');
  const [secondPartAddress, setSecondPartAddress] = useState('');

  useEffect(() => {

     const init = async() => {
       const web3 = await getWeb3();
       const accounts = await web3.eth.getAccounts();
       const usersAccount = accounts[0];
       const stringOfUsersAccount = new String(usersAccount)
       console.log(stringOfUsersAccount.slice(0, 4)); // "01"
       setFirstPartAddress(stringOfUsersAccount.slice(0, 5));
       setSecondPartAddress('____'+stringOfUsersAccount.slice(37, 42));

     }
     init()
   });

  const connectWallet = async(e) => {
    await wallet.connect()
    const usersAccount = props.accounts;
    const stringOfUsersAccount = new String(usersAccount)
    console.log(stringOfUsersAccount.slice(0, 4)); // "01"
    setFirstPartAddress('Welcome');
    setSecondPartAddress('');
    window.location.reload();

  }

  return(
    <div>
      <Button onClick={connectWallet} id="wallet-button" className="customButton enableEthereumButton">{firstPartAddress}{secondPartAddress}</Button>
      <>
      <h1>use-wallet</h1>

      {(() => {
        if (wallet.error?.name) {
          return (
            <p>
              <span>
                {wallet.error instanceof ConnectionRejectedError
                  ? 'Connection error: the user rejected the activation'
                  : wallet.error.name}
              </span>
              <button onClick={() => wallet.reset()}>retry</button>
            </p>
          )
        }

        if (wallet.status === 'connecting') {
          return (
            <p>
              <span>Connecting to {wallet.connector}…</span>
              <button onClick={() => wallet.reset()}>cancel</button>
            </p>
          )
        }

        if (wallet.status === 'connected') {
          return (
            <p>
              <span>Connected.</span>
              <button onClick={() => wallet.reset()}>disconnect</button>
            </p>
          )
        }

        return (
          <div className="connect">
            <div className="connect-label">Connect:</div>
            <div className="connect-buttons">
              <button onClick={() => activate('injected')}>injected</button>
              <button onClick={() => activate('frame')}>frame</button>
              <button onClick={() => activate('portis')}>portis</button>
              <button onClick={() => activate('fortmatic')}>fortmatic</button>
              <button onClick={() => activate('torus')}>torus</button>
              <button onClick={() => activate('walletconnect')}>
                walletconnect
              </button>
              <button onClick={() => activate('walletlink')}>walletlink</button>
            </div>
          </div>
        )
      })()}

      {wallet.status === 'connected' && (
        <>
          <p>
            <span>Account:</span>
            <span>{wallet.account}</span>
          </p>
        </>
      )}

      {wallet.account && (
        <p>
          <span>Balance:</span>
          <span>
            {wallet.balance === '-1'
              ? '…'
              : TokenAmount.format(wallet.balance, 18, { symbol: 'ETH' })}
          </span>
        </p>
      )}

      {wallet.status === 'connected' && (
        <p>
          <span>Block:</span> <span>{blockNumber || '…'}</span>
        </p>
      )}
    </>
    </div>
  )
}

// Wrap everything in <UseWalletProvider />
export default (props) => (
  <UseWalletProvider

  autoConnect
  connectors={{
    injected: {
      //allows you to connect and switch between mainnet and rinkeby within Metamask.
      chainId: [1, 4],
    },
    fortmatic: {
      chainId: [1],
      apiKey: '',
    },
    portis: {
      dAppId: '',
      chainId: [1],
    },
    walletconnect: {
      rpc: {
        1: 'https://mainnet.infura.io/v3/a0d8c94ba9a946daa5ee149e52fa5ff1',
        4: 'https://rinkeby.infura.io/v3/a0d8c94ba9a946daa5ee149e52fa5ff1',
      },
      bridge: 'https://bridge.walletconnect.org',
      pollingInterval: 12000,
    },
    walletlink: {
      chainId: [1],
      url: 'https://mainnet.eth.aragon.network/',
    },
  }}

    >







    <Connection accounts={props.accounts}/>


  </UseWalletProvider>
)
