import Web3 from "web3";


import Connection from "./Connection";

const getWeb3 = () =>
  new Promise((resolve, reject) => {
    // Wait for loading completion to avoid race conditions with web3 injection timing.
    window.addEventListener("load", async () => {
      // Modern dapp browsers...
      if (window.ethereum) {

        const Moralis = require('moralis');
        // ES6 Minimized
        const web3 = await Moralis.enableWeb3({ provider: "walletconnect" });
        // const users = new Web3(window.ethereum);

        // Add a network
                // const chainId = 43114;
                // const chainName = "Avalanche Mainnet";
                // const currencyName = "AVAX";
                // const currencySymbol = "AVAX";
                // const rpcUrl = "https://api.avax.network/ext/bc/C/rpc";
                // const blockExplorerUrl = "https://cchain.explorer.avax.network/";
                // await Moralis.addNetwork(
                //   chainId,
                //   chainName,
                //   currencyName,
                //   currencySymbol,
                //   rpcUrl,
                //   blockExplorerUrl
                // );


                // Network switcher
                    // const chainId = "0x1"; //Ethereum Mainnet
                    const chainId = "0x38"; //Meter Mainnet
                    // const chainId = "0x52"; //Meter Mainnet

                    const chainIdHex = await Moralis.switchNetwork(chainId);
                    Connection()
        try {
          // Request account access if needed



          await window.ethereum.enable();

          // Accounts now exposed
          resolve(web3);


        } catch (error) {
          reject(error);
        }
      }
      // Legacy dapp browsers...
      else if (window.web3) {
        // Use Mist/MetaMask's provider.
        const web3 = window.web3;
        console.log("Injected web3 detected.");
        resolve(web3);

      }
      // Fallback to localhost; use dev console port by default...
      else {

        // const provider = new Web3.providers.HttpProvider('https://mainnet.meter.io:8667');

        const provider = new Web3.providers.HttpProvider('https://data-seed-prebsc-1-s1.binance.org:8545/');

        const web3 = new Web3(provider);
        console.log("No web3 instance injected, using Local web3.");
        resolve(web3);
      }
    });
  });


export default getWeb3;
