import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,Row,Col,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown,ProgressBar } from 'react-bootstrap';
import moment from 'moment';

import img1 from './assets/ExampleGameBackground.png';
// import img1 from '../images/elons_lost_his_rocket.png';

import React, { Component, useState, useEffect } from 'react'
import Connection from "../Connection";
import { useWallet, UseWalletProvider } from 'use-wallet'
import './Partner.css';

import Timer from '../components/Structure/PartnerExampleGame1Clone/Timer';

import GameCard from '../components/Structure/GameCard';

import HomeHuntCards from '../components/Structure/HomeHuntCards';

import EnterHuntCards from '../components/Structure/PartnerExampleGame1Clone/EnterHuntCards';
import LeaderBoardCard from '../components/Structure/PartnerExampleGame1Clone/LeaderBoardCard';


import Confetti from 'react-confetti'
import { Helmet } from 'react-helmet';
// Hook
function useWindowSize() {
  // Initialize state with undefined width/height so server and client renders match
  // Learn more here: https://joshwcomeau.com/react/the-perils-of-rehydration/
  const [windowSize, setWindowSize] = useState({
    width: undefined,
    height: undefined,
  });

  useEffect(() => {
    // Handler to call on window resize
    function handleResize() {
      // Set window width/height to state
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    // Add event listener
    window.addEventListener("resize", handleResize);
    // Call handler right away so state gets updated with initial window size
    handleResize();
    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleResize);
  }, []); // Empty array ensures that effect is only run on mount
  return windowSize;
}









const PartnershipHunt1 = (props) => {



  const commaNumber = require('comma-number')
  const { width, height } = useWindowSize();

  const game1_prize = commaNumber(props.game1_prize,',');
  const [loading,setloading] = useState(false);


  const [playLevel1Game1,setplayLevel1Game1] = useState(false);
  const [redirectLevel2,setredirectLevel2] = useState(false);



  const handlePayQuest = async() => {

    window.dataLayer.push({
      event: "wallet_information",
      wallet: props.wallet_for_google,
      wallet_ip: props.ip,
      url: window.location.pathname,
      buttonClicked:"Play_MiniGame_Game1_Level_1"
    });

    setloading(true);
    const quest = await props.contract.methods.makePaymentPlayQuest(props.allGame1_id).send({from: props.accounts});
    setplayLevel1Game1(true)
    setredirectLevel1(true)

    setTimeout(function(){
        setloading(false);
    },30000);
  }






  const winner = props.treasure_found1;

    return (


      <div className="custombackground">

      {redirectLevel1?(
        <div>
        <Redirect to="/Level1Game1" {...props}/>
        </div>

      ):(
        <div>

        </div>
      )}


      <div className="spaceTopHome">
      <Helmet>
        <title>TreasureBlox | Elon's Rocket</title>
      </Helmet>
      {winner? (
        <div><Confetti
          width={width}
          height={height}
	        numberOfPieces={300}

          drawShape={ctx => {
            ctx.beginPath();
            for (var i = 0; i < 6; i++) {
              ctx.lineTo(10 + 20 * Math.cos(2 * Math.PI / 6 * i), 10 + 20 * Math.sin(2 * Math.PI / 6 * i));
            }
            ctx.closePath();
            ctx.stroke();
          }}
        />

        </div>

      ):(
        <div>

        </div>
      )}

      <div>
            <div className="siteTitle">Welcome, We will create a custom treasure hunt for your project?</div>

      <Container className='mt-5' fluid="md">



            <CardGroup className='mt-5'>

            <Row>
            <Col sm={4}>

            <EnterHuntCards {...props}/>

            </Col>

            <Col sm={8}>
            <LeaderBoardCard {...props}/>
            </Col>
            </Row>
            <br/>

            <Row>

            <Col sm={3}>


            <Card className='cardRounded border border-danger'>
            <Card.Img variant="top" src={img1} alt="Logo" className='cardRoundedMiniGame'/>

              <Card.Body className="customBodyMiniGame">
                <Card.Header className="MiniGameTitle">PLAY Level 1 Mini Game

                </Card.Header>
                <br/>
                <div className="MiniGameSubTitle">

                Play this game to earn the level 1 riddle!



                </div>
                <br/>
                <Button className="customButton" onClick={handlePayQuest}>Play For L1 Clue</Button>


                <footer className="blockquote">
                </footer>

                <br />


              </Card.Body>
            </Card>

            </Col>

            <Col sm={3}>

            <Card className='cardRounded border border-danger'>
            <Card.Img variant="top" src={img1} alt="Logo" className='cardRoundedMiniGame'/>

              <Card.Body className="customBodyMiniGame">
                <Card.Header className="MiniGameTitle">PLAY Level 2 Mini Game

                </Card.Header>
                <br/>
                <div className="MiniGameSubTitle">

                Play this game to earn the level 1 riddle!



                </div>
                <br/>
                <Button className="customButton" onClick={handlePayQuest}>Play For L2 Clue</Button>


                <footer className="blockquote">
                </footer>

                <br />


              </Card.Body>
            </Card>

            </Col>
            <Col sm={3}>

            <Card className='cardRounded border border-danger'>
            <Card.Img variant="top" src={img1} alt="Logo" className='cardRoundedMiniGame'/>

              <Card.Body className="customBodyMiniGame">
                <Card.Header className="MiniGameTitle">PLAY Level 3 Mini Game

                </Card.Header>
                <br/>
                <div className="MiniGameSubTitle">

                Play this game to earn the level 1 riddle!



                </div>
                <br/>
                <Button className="customButton" onClick={handlePayQuest}>Play For L3 Clue</Button>


                <footer className="blockquote">
                </footer>

                <br />


              </Card.Body>
            </Card>

            </Col>

            <Col sm={3}>

            <Card className='cardRounded border border-danger'>
            <Card.Img variant="top" src={img1} alt="Logo" className='cardRoundedMiniGame'/>

              <Card.Body className="customBodyMiniGame">
                <Card.Header className="MiniGameTitle">PLAY Level 4 Mini Game

                </Card.Header>
                <br/>
                <div className="MiniGameSubTitle">

                Play this game to earn the level 1 riddle!



                </div>
                <br/>
                <Button className="customButton" onClick={handlePayQuest}>Play For L4 Clue</Button>


                <footer className="blockquote">
                </footer>

                <br />


              </Card.Body>
            </Card>

            </Col>
            </Row>



            </CardGroup>

        </Container>


      </div>



</div>
</div>


    );

}

export default PartnershipHunt1;
