  let startDate = null;
  function millisecondsToMinutes(millis){
      let minutes = Math.floor(millis / 60000);
      let seconds = ((millis % 60000) / 1000).toFixed(0);
      return minutes + ":" + (seconds < 10 ? "0" : "") + seconds;
  }

  function gameOver() {
      if(!hasGameOver){
          let endDate = new Date();
          let timeDiff = endDate - startDate;
          if(timeDiff < 60000){
              alert(`You survived ${timeDiff / 1000} seconds!`);
          }else{
              let minutesSurvived = millisecondsToMinutes(timeDiff);
              alert(`You survived ${minutesSurvived} minutes. WOW!`);
          }

          hasGameOver = true;
      }
  }

  //Canvas Black Border
  function drawBorder() {
      ctx.fillStyle = 'black';
      ctx.fillRect(0,0,canvas.width,canvas.height);
      ctx.clearRect(50,50,500,500);
  }

  let playerRect = {
      x: 275,
      y: 275,
      width: 50,
      height: 50
  }
  //Game Elements
  let rectangles = [
      {
          x: 75,
          y: 75,
          dx: 5,
          dy: 4,
          width: 75,
          height: 75,
          color: "#000099"
      },
