import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import MyNav from './components/Structure/Nav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component } from 'react'
import Web3 from 'web3'



class App extends Component {
  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    console.log("Accounts: ",accounts)
    buttonText = account;
  }



  constructor(props) {
    super(props)
    this.state = { account: '' };
    this.state = {isToggleOn: true};

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn
    }));
  }

  componentWillMount() {
    this.loadBlockchainData()
  }















  render() {

    return (
        <div className="background customFont">

          <MyNav account={buttonText}/>
          <SingleCard/>
          <Button onClick={this.handleClick}>
                  {this.state.isToggleOn ? 'ON' : 'OFF'}
                </Button>

          <MyCards/>
          <MyCards/>
      </div>
    );
  }
}

export default App;
