import React, { Component, useState, useEffect } from 'react'
import ReactDOM from 'react-dom';
import Countdown from 'react-countdown';
import moment from 'moment';


import { CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown,Row,Col } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './Card.css';

import Step1Card from "./Step1Card";
import Step2Card from "./Step2Card";
import Step3Card from "./Step3Card";
import Step3CardNoEntry from "./Step3CardNoEntry";


const Timer = (props) => {

  // const game1_head_start_time = props.game1_head_start_time;
  // const game1_entry_limit = props.game1_entry_limit;
  // const game1_entry_cost = props.game1_entry_cost;



// DO CHECKS WITH THE USER
  // Check the game is live
  const game1_live = props.game1_live;
  // const game1_live = true;
  // Enter Game
  const enter = props.userEntered_game1;
  // if that user is the user who entered the game this works
  // const enter = true;
  // Head Start Time Lock
  const timeLock = props.countGame1DeadlineTrue;


  if (props.accounts === props.attemptAddress){
    const userActivatedTimeLock = true;

  } else {

    const userActivatedTimeLock = false;
  }


  // const timeLock = true;
  console.log("time lock: ", timeLock)

  // Submit Secret
  const winner = false;


  // console.log("game entered:", userEntered_game1)



// padlock
// https://codepen.io/RCMiron/pen/bYPqPp
// https://threejs.org/docs/index.html#manual/en/introduction/Creating-a-scene




  return(


    // Enter Step 1
<div>

    {game1_live ?(


      <div>



      <div>

        {enter ? (


          <div>

          <div>
                  {timeLock ? (
                    <div>
                    {userActivatedTimeLock ?(
                      <div>

                        { winner ?(

                            <div>
                            if the wallet address is user and winner congratulations
                            You are a winner congratulations message based on submit secret state change. check fun in js and set off confeti bomb? and sound bomb
                            check what happens for others when things submitted
                            </div>
                        ):(

                            <div className="descriptionTitle">

                            <Step3Card
                            web3={props.web3}
                            game1_id={props.game1_id}
                            game1_live={props.game1_live}
                            game1_prize={props.game1_prize}
                            game1_question_hash={props.game1_question_hash}
                            game1_time_lock_cost={props.game1_time_lock_cost}
                            game1_submit_secret_cost={props.game1_submit_secret_cost}
                            game1_riddle={props.game1_riddle}
                            game1_clue={props.game1_clue}
                            game1_head_start_time={props.game1_head_start_time}
                            game1_entry_limit={props.game1_entry_limit}
                            game1_entry_cost={props.game1_entry_cost}
                            allGame1_id={props.allGame1_id}
                            allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
                            allGame1_deadline_time={props.allGame1_deadline_time}
                            allGame1_username={props.allGame1_username}
                            allGame1_total_game_tries={props.allGame1_total_game_tries}
                            countGame1={props.countGame1}
                            countGame1DeadlineTrue={props.countGame1DeadlineTrue}
                            decimals={props.decimals}
                            totalTreasure={props.totalTreasure}
                            contract={props.contract}
                            accounts={props.accounts}
                            game1numberOfEntries={props.game1numberOfEntries}
                            userGame1_id={props.userGame1_id}
                            userEntered_game1={props.userEntered_game1}
                            userGame1_headstart_time={props.userGame1_headstart_time}
                            userGame1_live={props.userGame1_live}/>
                            </div>
                        )}

                      </div>
                    ):(
                      <div>

                      <Step3CardNoEntry
                      web3={props.web3}
                      game1_id={props.game1_id}
                      game1_live={props.game1_live}
                      game1_prize={props.game1_prize}
                      game1_question_hash={props.game1_question_hash}
                      game1_time_lock_cost={props.game1_time_lock_cost}
                      game1_submit_secret_cost={props.game1_submit_secret_cost}
                      game1_riddle={props.game1_riddle}
                      game1_clue={props.game1_clue}
                      game1_head_start_time={props.game1_head_start_time}
                      game1_entry_limit={props.game1_entry_limit}
                      game1_entry_cost={props.game1_entry_cost}
                      allGame1_id={props.allGame1_id}
                      allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
                      allGame1_deadline_time={props.allGame1_deadline_time}
                      allGame1_username={props.allGame1_username}
                      allGame1_total_game_tries={props.allGame1_total_game_tries}
                      countGame1={props.countGame1}
                      countGame1DeadlineTrue={props.countGame1DeadlineTrue}
                      decimals={props.decimals}
                      totalTreasure={props.totalTreasure}
                      contract={props.contract}
                      accounts={props.accounts}
                      game1numberOfEntries={props.game1numberOfEntries}
                      userGame1_id={props.userGame1_id}
                      userEntered_game1={props.userEntered_game1}
                      userGame1_headstart_time={props.userGame1_headstart_time}
                      userGame1_live={props.userGame1_live}
                      attemptId={props.attemptId}
                      attemptAddress={props.attemptAddress}
                      attemptUsername={props.attemptUsername}
                      attemptDeadline={props.attemptDeadline}
                      />


                      </div>
                    )}
                    </div>

                  ) : (

                    <div>

                      <Step2Card
                      web3={props.web3}
                      game1_id={props.game1_id}
                      game1_live={props.game1_live}
                      game1_prize={props.game1_prize}
                      game1_question_hash={props.game1_question_hash}
                      game1_time_lock_cost={props.game1_time_lock_cost}
                      game1_submit_secret_cost={props.game1_submit_secret_cost}
                      game1_riddle={props.game1_riddle}
                      game1_clue={props.game1_clue}
                      game1_head_start_time={props.game1_head_start_time}
                      game1_entry_limit={props.game1_entry_limit}
                      game1_entry_cost={props.game1_entry_cost}
                      allGame1_id={props.allGame1_id}
                      allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
                      allGame1_deadline_time={props.allGame1_deadline_time}
                      allGame1_username={props.allGame1_username}
                      allGame1_total_game_tries={props.allGame1_total_game_tries}
                      countGame1={props.countGame1}
                      countGame1DeadlineTrue={props.countGame1DeadlineTrue}
                      decimals={props.decimals}
                      totalTreasure={props.totalTreasure}
                      contract={props.contract}
                      accounts={props.accounts}
                      game1numberOfEntries={props.game1numberOfEntries}/>

                    </div>

                  )}
          </div>

          </div>



        ) : (

            <div>
            <Step1Card
            game1_id={props.game1_id}
            game1_live={props.game1_live}
            game1_prize={props.game1_prize}
            game1_question_hash={props.game1_question_hash}
            game1_time_lock_cost={props.game1_time_lock_cost}
            game1_submit_secret_cost={props.game1_submit_secret_cost}
            game1_riddle={props.game1_riddle}
            game1_clue={props.game1_clue}
            game1_head_start_time={props.game1_head_start_time}
            game1_entry_limit={props.game1_entry_limit}
            game1_entry_cost={props.game1_entry_cost}
            allGame1_id={props.allGame1_id}
            allGasme1_user_front_of_que={props.allGame1_user_front_of_que}
            allGame1_deadline_time={props.allGame1_deadline_time}
            allGame1_username={props.allGame1_username}
            allGame1_total_game_tries={props.allGame1_total_game_tries}
            countGame1={props.countGame1}
            countGame1DeadlineTrue={props.countGame1DeadlineTrue}
            decimals={props.decimals}
            totalTreasure={props.totalTreasure}
            contract={props.contract}
            accounts={props.accounts}
            game1numberOfEntries={props.game1numberOfEntries}
            userGame1_id={props.userGame1_id}
            userEntered_game1={props.userEntered_game1}
            userGame1_headstart_time={props.userGame1_headstart_time}
            userGame1_live={props.userGame1_live}/>
            </div>


        )}

        </div>





      </div>



    ):(



      <div>

      {props.treasure_found?(
        <div>
        Congratulations a treasure hunter helped Elon find his Rocket!
        <div className="largeEnter">WINNER</div>
        <div className="gameTitleEnter">Prize Won {props.winning_prize} Blox</div>

        <br/>
        <div className="enterGameTitle">Winning Message</div>
        <div className="descriptionTitle">{props.winning_message}</div>

        <br/><br/>
        <div className="enterGameTitle">Winning Address</div>
        {props.winning_address}

        </div>
      ):(
        <div>

        We are launching this treasure quest very soon!
        <div className="largeEnter">WATCH THIS SPACE</div>

        </div>

      )}



      </div>


    )}
</div>



  );

};

export default Timer;
