import { Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import MyNav from './components/Structure/Nav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component } from 'react'
import getWeb3 from "./getWeb3";


// Get network provider and web3 instance.
const web3 = await getWeb3();

// Use web3 to get the user's accounts.
const accounts = await web3.eth.getAccounts();


// Set the provider
web3.setProvider(new web3.providers.WebsocketProvider('ws://localhost:8545')
);

const abi = require('./TreasureBlox.abi.json');
const mepAddress = '0xd9145CCE52D386f254917e481eB44e9943F39138'

let instance = new web3.eth.Contract(abi,mepAddress);






class App extends Component {


  state = {
     web3: null,
     accounts: null,
     contract: null,
     decimals:null,
     bal:null,
     owner:null,
     marketingWallet:null,
     games:[]};

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();


      // Set the provider
      web3.setProvider(new web3.providers.WebsocketProvider('ws://localhost:8545')
      );

      const abi = require('./TreasureBlox.abi.json');
      const mepAddress = '0xd9145CCE52D386f254917e481eB44e9943F39138'

      let instance = new web3.eth.Contract(abi,mepAddress);

      // // Get the contract instance.
      // const networkId = await web3.eth.net.getId();
      // const deployedNetwork = TreasureBlox.networks[networkId];
      // const instance = new web3.eth.Contract(
      //   TreasureBlox.abi,
      //   deployedNetwork && deployedNetwork.address,
      // );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;



    const decimals = await contract.methods.decimals().call().then(console.log('Yes'));

    const bal = await contract.methods.balanceOf(accounts[0]).call();

    const owner = await contract.methods._owner().call();

    const marketingWallet = await contract.methods.marketingWallet().call().then(console.log('marketing Wallet Complete'));

    const games = await contract.methods.Games(1).call().then(console.log('games check complete'));

    // const games = await contract.methods.Games(1).call();


    // Update state with the result.
    this.setState({
      account: accounts[0],
      decimals:decimals,
      bal:bal,
      owner:owner,
      marketingWallet:marketingWallet,
      games:games});
  };


  render() {

    return (
        <div className="background customFont">

          <MyNav />
          <SingleCard decimals={this.state.storageValue}/>

          <br/>
          <div>The balance value is: {this.state.bal}</div>
          <div>The owner value is: {this.state.owner}</div>
          <div>The marketingWallet value is: {this.state.marketingWallet}</div>
          <div>The games values are: {this.state.games[0]}</div>
          <br/>





          <MyCards/>
          <MyCards/>
      </div>
    );
  }
}

export default App;
