import MyNav from './MyNav';
import MyCards from './components/Structure/Cards';
import SingleCard from './components/Structure/SingleCard';
import moment from 'moment';

// import img1 from './Treasure-hunt.jpeg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import React, { Component, useState, useEffect } from 'react'
import Connection from "./Connection";
import { useWallet, UseWalletProvider } from 'use-wallet'
import "./App.css";

import Timer from './components/Structure/Timer';

import img1 from './components/Structure/images/elons-rocket.jpg';
import img2 from './components/Structure/images/fortblox.png';



import {Tooltip,OverlayTrigger,Form,ButtonGroup,ButtonToolbar,CardColumns,CardGroup,Card,Button,Container,Nav,Navbar,NavDropdown } from 'react-bootstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import './components/Structure/HomeCard.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";



const Home = (props) => {

  const commaNumber = require('comma-number')


  const game1_id = props.game1_id;
  const game1_live = props.game1_live;
  const game1_prize = commaNumber(props.game1_prize,',');
  const game1_question_hash = props.game1_question_hash;
  const game1_cost_to_enter = props.game1_cost_to_enter;
  const game1_submit_secret_cost = props.game1_submit_secret_cost;
  const game1_clue = props.game1_clue;


  const allGame1_deadline_time = props.allGame1_deadline_time;

  // console.log(game1_id)s

  const renderTooltip = (props) => (


  <Tooltip id="button-tooltip" {...props}>
    Once you have purchased your Blox they buy you 30 seconds to enter your answer, no one else can answer in this time and this stops bots from beating you to it!
  </Tooltip>
);




    return (

      <div>
      <Container className='mt-5' fluid="md">

  <div className="spaceTop">

        <div className="siteTitle">TreasureBlox - The Worlds biggets internet and real world crypto treasure hunt.</div>
        <CardGroup className='mt-5'>

          <CardColumns >


            <Card className='cardRounded'>
              <Card.Body className="customBodyThree">
                <Card.Title className="gameTitle">How it works?</Card.Title>

                <div className="descriptionTitle">Step 1</div>
                <div className="descriptionBody">Purchase Blox, enter a treasure quest maybe even purchase a head start. All quests have different entry requirements. Token holders with more than 1Billion tokens are automatically entered and get head starts on all games. </div>
                <div className="descriptionTitle">Step 2</div>
                <div className="descriptionBody">Think you know the answer? Purchase a time lock for 90 seconds where you are the only player who can answer the secret.</div>
                <div className="descriptionTitle">Final Step</div>
                <div className="descriptionBody">Once the Time lock is purchased you can enter your secret. If you win so does the community as 10x the prize is burnt from the total token supply.</div>
                <br/>
                <Link to="/hunt1">
                  <Button className="tinyCustomButtonDescription" to="/hunt1">Contract</Button>
                </Link>

                <Link to="/hunt1">
                  <Button className="tinyCustomButtonDescription" to="/hunt1">White Paper</Button>
                </Link>

                <Link to="/hunt1">
                  <Button className="tinyCustomButtonDescription" to="/hunt1">Telegram</Button>
                </Link>

              </Card.Body>
            </Card>

            <Card className='cardRounded'>
              <Card.Body className="customBodyTwo">
                <Card.Title className="gameTitle">Purchase Blox</Card.Title>
                <div className="descriptionTitle">Total Treasure Chest {props.game1_prize} Blox</div>
                <div >Purchase your Blox to enter a quest </div>
                <br/>
                <Button className="customButton" >Buy Blox</Button>

                <br/><br/>

                <div className="postPrize">Total Supply {props.allGame1_total_game_tries}</div>
              </Card.Body>
            </Card>


            <Card className='cardRounded'>
              <Card.Img variant="top" src={img1} alt="Logo" className='customRounded'/>
              <Card.Body >
                <Card.Title className="gameTitle">Elon's lost his Rocket!</Card.Title>
                <div>Riddle {props.game1_riddle}</div>
                <div>Prize {props.game1_prize} Blox</div>
                <div>Number of people entered</div>
                <br/>
                <Link to="/hunt2">
                  <Button className="customButton" to="/hunt1">View Elon's Quest</Button>
                </Link>
                <br/><br/>

                <div className="postPrize">{props.allGame1_total_game_tries} Attempts <div className="prePrize">Hunt Launched 1 day ago</div></div>
              </Card.Body>
            </Card>

            <Card className='cardRounded'>
              <Card.Img variant="top" src={img2} alt="Logo" className='customRounded'/>
              <Card.Body >
                <Card.Title className="gameTitle">Fort Blox</Card.Title>
                <div>Riddle {props.game1_riddle}</div>
                <div>Prize {props.game1_prize} Blox</div>
                <div>Number of people entered</div>
                <br/>
                <Link to="/hunt3">
                  <Button className="customButton" to="/hunt1">View Fort Blox Quest</Button>
                </Link>
                <br/><br/>
                <div className="postPrize">{props.allGame1_total_game_tries} Attempts <div className="prePrize">Hunt Launched 1 day ago</div></div>
              </Card.Body>
            </Card>


            </CardColumns>
        </CardGroup>

      </div>
      </Container>
      </div>


    );

}

export default Home;
