          //   <a className='neon-blue' style={{"text-decoration": "none"}}> Gun</a>
          // </div>

          ctx.font = "50px neon-blue";
          ctx.fillStyle = "#fb4264";

          ctx.fillText("REVOLVER ROULETTE", 50, 50);
          ctx.font = "30px neon-blue";

          ctx.fillStyle = "#fb4264";
          ctx.fillText((winnings/2), 175, 100);

          // ctx.fillStyle = #fb4264;
